        for (int i = 0; i < nums.size(); i++)   prefix[i + 1] = prefix[i] + nums[i];
        for (int i = 1; i <= n - 2; i++) {
            int diff = prefix[n] - prefix[i];
            int mid = diff / 2;
            if (mid < prefix[i])    break;
            mid += prefix[i];
