class Solution {
    private static final int MOD = 1000000007;

    // O(N*LogN)
    public int waysToSplit(int[] nums) {
        int[] summary = new int[nums.length];
        int totalSummary = 0;
        for (int i = 0; i < nums.length; i++) {
            totalSummary += nums[i];
            summary[i] = totalSummary;
        }
        int ways = 0;
        // i: right bound of part1
        for (int i = 0; i < nums.length-2 && summary[i] <= totalSummary/3; i++) {
            // binary-search
            int left = summary[i];
            // find splitIndexMax which part2 ≤ part3
            int l = i+1, r = nums.length-2;
            while (l <= r) {
                int m = (l+r)>>1;
                int mTemp = summary[m] - left;         // [i+1, m]
                int rTemp = totalSummary - summary[m]; // [m+1, nums.length-1]
                if (mTemp <= rTemp) {
                    // m, satisfy condition
                    l = m+1;
                } else {
                    r = m-1;
                }
            }
            // if l-1 in [i+1, nums.length-2], it's a valid split
            if (l-1 < i+1 || l-1 > nums.length-2) {
                continue;
            }
            int splitIndexMax = l-1;

            // find splitIndexMin which part1 ≤ part2
            l = i+1;
            r = splitIndexMax;
            while (l <= r) {
                int m = (l+r)>>1;
                int mTemp = summary[m] - left; // [i+1, m]
                if (mTemp >= left) {
                    // m, satisfy condition
                    r = m-1;
                } else {
                    l = m+1;
                }
            }
            // if r+1 in [i+1, splitIndexMax], it's a valid split
            if (r+1 < i+1 || r+1 > splitIndexMax) {
                continue;
            }
            int splitIndexMin = r+1;
            // add to ways and make mod
            ways += splitIndexMax - splitIndexMin + 1;
            ways %= MOD;
        }
        return ways;
    }
}
