            int mi = lo + (hi - lo) / 2;
            int mid = nums[mi] - min;
            int max = nums[right] - nums[mi];
            if (mid < min) {
                lo = mi + 1;
            } else if (max < mid){
                hi = mi - 1;
            } else {
                pos = mi;
                lo = mi + 1;
            }
        }
        return pos;
    }

}
