        ll mod=1e9+7;
        ll n=nums.size();
        vector<ll> prefix(n,0);
        prefix[0]=nums[0];
        for(ll i=1;i<n;i++)
        {
            prefix[i]=prefix[i-1]+nums[i];
        }
