        ll total=prefix[n-1];
        for(int i=0;i<n-2;i++)
        {
            if(prefix[i]>total/3)
            break;
            ll j=lower_bound(prefix.begin()+i+1,prefix.end(),prefix[i]*2)-prefix.begin();
            ll l=upper_bound(prefix.begin()+i+1,prefix.end(),(total-prefix[i])/2+prefix[i])-prefix.begin();
            if(l<j)
