    }

    midLeftIdx, midRightIdx := -1, -1

    numWaysToSplit := 0
    for leftIdx := 0; leftIdx < lastIdx; leftIdx++ {
        leftSum += nums[leftIdx]
        midLeftSum -= nums[leftIdx];
        midRightSum -= nums[leftIdx];

        // find the first index that the middle section is valid
        for (midLeftIdx <= lastIdx && 
               (midLeftIdx <= leftIdx || midLeftSum < leftSum)) {
            midLeftIdx++;
            midLeftSum += nums[midLeftIdx]
        }

        // find the first index that makes the comparison between the
        // middle and right index invalid
        for (midRightIdx <= lastIdx && 
               (midLeftIdx > midRightIdx || midRightSum <= total - midRightSum - leftSum)) {
            midRightIdx++
            midRightSum += nums[midRightIdx]
        }
        numWaysToSplit = (numWaysToSplit + midRightIdx - midLeftIdx) % mod;
    }
    return numWaysToSplit
}
