```
class Solution {
public:
    int waysToSplit(vector<int>& nums) {
        long n=nums.size(), M=1000000007;
        vector<long> pref(n);
        pref[0]=nums[0];

        for(long i=1; i<n; ++i) pref[i]=pref[i-1]+nums[i];

        long res=0;

        for(long i=0; i<n-2; ++i){
			//lsum is sum of left segment
            long lsum=pref[i];
			//mr is end point of middle segment
            long mr= lower_bound(pref.begin(), pref.end(), 2*lsum)-pref.begin();
            mr=max(mr, i+1);
            if(mr>=n-1) continue;

			//Binary search for possible values of mr such that Sum(mid)<=Sum(right)
            long l=mr, r=n-2, ans=0;
            while(l<=r){
                long mid=(l+r)/2;
                long Sm= pref[mid]-lsum;
                long Sr= pref[n-1]-pref[mid];

                if(Sm<=Sr) { ans=max(ans, mid); l=mid+1; }
                else r=mid-1;
            }
            // cout<<lsum<<" "<<mr<<" "<<ans<<endl;
            if(ans>=mr) res+=ans-mr+1;
            res%=M;
        }
        return res;
    }
};
