                int sum1 = pref[mid-1];
                int sum2 = pref[i-1]-sum1;
                if(sum1<=sum2 && sum2<=sum3 && sum1<=sum3)
                {
                    idx1 = mid;
                    l = mid+1;
                }
                else if(sum1>sum2)
                r = mid-1;
                else
                l = mid+1;
            }
            l = 1,r= i-1;
            while(l<=r)
            {
                int mid = (l+r)/2;
                int sum1 = pref[mid-1];
                int sum2 = pref[i-1]-sum1;
                if(sum1<=sum2 && sum2<=sum3 && sum1<=sum3)
                {
                    idx2 = mid;
                    r = mid-1;
                }
                else if(sum1>sum2)
                r = mid-1;
                else 
                l = mid+1;
            }
            if(idx1!=idx2 && idx1!=-1 && idx2!=-1)
            {
                ans = (ans+((idx1-idx2)+1))%mod;
            }
            else if(idx1!=-1 || idx2!=-1)
            {
                ans = (ans+1)%mod;
            }
        }
        return ans;
    }
};

