using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace _1712_ways_to_split
{
    class Program
    {
        static void Main(string[] args)
        {
        }
        /// <summary>
        /// study code
        /// https://leetcode.com/problems/ways-to-split-array-into-three-subarrays/discuss/999257/C%2B%2BJavaPython-O(n)-with-picture
        /// time complexity: O(N)
        /// </summary>
        /// <param name="nums"></param>
        /// <returns></returns>
        public int WaysToSplit(int[] nums)
        {
            var length = nums.Length;
            var count = 0;

            for (int i = 1; i < length; ++i)
            {
                nums[i] += nums[i - 1];
            }

            // i < j < k
            for (int i = 0, j = 0, k = 0; i < length - 2; ++i)
            {
                // j - maximum count: O(N) - make sure that j > i, but nums[j] >= nums[i] * 2
                while (j <= i || (j < length - 1 && nums[j] < nums[i] * 2))
                {
                    ++j;
                }

                // k - make sure that k >= j, but rightmost subarray's sum is bigger than second one
                while (k < j || (k < length - 1 && nums[k] - nums[i] <= nums[length - 1] - nums[k]))
                {
                    ++k;
