const int MOD = (int)(1e9) + 7 ;
class Solution {
public:
    vector<int> getPrefix(vector<int> &a, int &n){
        vector<int> prefix(n, 0) ; prefix[0] = a[0] ;
        for(int i = 1 ; i < n ; i++) prefix[i] = prefix[i-1] + a[i] ;
        return prefix ;
    }
    int waysToSplit(vector<int>& nums) {
        int len = nums.size(), res = 0 ;
        vector<int> prefix = getPrefix(nums, len) ;
        for(int i = 0 ; i <= len - 1 ; i++){
            int cur = prefix[i] ;
            int mnMid = cur ;
            int mxMid = ( prefix[len-1] - cur ) / 2 ;
            if( cur > mxMid ) break ;
            int j = lower_bound(prefix.begin() + i + 1, prefix.end() - 1, cur + mnMid ) - prefix.begin() ;
            int k = upper_bound(prefix.begin() + i + 1, prefix.end() - 1, cur + mxMid ) - prefix.begin() - 1 ;
            res = ( res + k - j + 1 ) % MOD ;
        }
        return res ;
