        // Ex: [1,2,2,2,5,0]: A possible cut: [1,2] [2,2], [5,0] --> here the array sum is in sorted order: 3,4,5 so we apply binary search on prefix sum array

        for(int i = 0; i<s; i++) {

            //1. the minimum sum of middle array can be sum[i]. So we find the first index j at which the sum is >= sum[i]
             int a1 = binarySearch1(i+1, s-1, i, sum);
             if(a1 == -1) continue;

            //2. the max sum of middle arr can be reminSum/2. So we find the first index k at which the sum <= remainingSum/2
            int a2 = binarySearch2(i+1, s-1, i, s, sum);
            if(a2 == -1 || a2 < a1) continue;
            if(a2 == s-1) a2 -= 1;

            //3. Now the indices range [a1 a2] are the ways we can break the array. Store it in ans 

            ans += (a2-a1+1);
        }

        return ans%(1000000007);
    }

    int binarySearch1(int left, int right, int i, vector<int>& sum) {

         int ans1 = -1;
        while(left<=right) {
            int mid = left + (right-left)/2;

            if(sum[mid]-sum[i] >= sum[i]) {
                // possible ans
                ans1 = mid;
                right = mid-1;
            } else {
                left = mid+1;
            }
        }
        return ans1;
    }

    int binarySearch2(int left, int right, int i, int sz, vector<int>& sum) {

        int ans2 = -1;
        while(left<=right) {
            int mid = left + (right-left)/2;

            if(sum[mid]-sum[i] <= (sum[sz-1] - sum[i])/2) {
                //possible ans
                ans2 = mid;
                left = mid+1;
            } else {
                right = mid-1;
            }
        }
        return ans2;
    }
};
