class Solution {
    static final int MOD=1000000007;
    public int waysToSplit(int[] nums) {
        var len=nums.length;
        for(int i=1;i<len;i++) {
            nums[i]+=nums[i-1];
        }
        int l=0,r=0,cnt=0;
        for(int i=0;i<len;i++) {
            l=Math.max(i+1,l);
            while(l<len&&nums[l]-nums[i]<nums[i]) l++;
            r=Math.max(l,r);
            while(r<len-1&&nums[len-1]-nums[r]>=nums[r]-nums[i]) r++;
            cnt+=r-l;
            cnt%=MOD;
        }
        return cnt;
    }
}
