class Solution {
    public int waysToSplit(int[] nums) {
        int ans=0;
        int MOD = (int) (1e9 + 7);
        for(int i=1; i<nums.length; i++){
            nums[i]+=nums[i-1];
        }
        for(int i=0; i<nums.length-1; i++){
            if (nums[i]>nums[nums.length-1]-nums[i])  break;
            int l=findind(nums, i, nums[i], true);
            int r=findind(nums, i, nums[i], false);
            if (l==-1 || r==-1) continue;
            ans = (ans + (r - l + 1) % MOD) % MOD;
        }
        return ans;
    }

    public int findind(int[] nums, int ind, int lsum, boolean goleft){
        int l=ind+1,r=nums.length-1, ans=-1;
        int N=nums.length;
        while(l<=r){
            int m=(l+r)/2;
            int msum=nums[m]-lsum,rsum=nums[N-1]-nums[m];
            if(m==N-1) break;
            if (lsum<=msum && msum<=rsum){
                ans=m;
                if (goleft) r=m-1;
                else l=m+1;
            }
            else if (msum>rsum) r=m-1;
            else  l=m+1;
        }
        return ans;
    }
}
