public:
    int waysToSplit(vector<int>& nums) {
        int s = 0;
        for (int x : nums) {
            s += x;
        }
        const int n = nums.size();
        long long r  = 0;
        for (int i = 0, s1 = 0, s2 = 0, s3 = s, num2 = 0, num3 = n; i < n; ++i) {
            s1 += nums[i];
            for (; num2 < n && (num2 <= i + 1 || s2 - s1 < s1); s2 += nums[num2++])
            ;
            if (num2 <= i + 1 || s2 - s1 < s1) break;
            for (; num3 > 1 && s - s1 - s3 + nums[n - num3] <= s3 - nums[n - num3]; s3 -= nums[n - num3--])
            ;
            if (num3 >= n - i - 1) break;
            // x >= num2 - 1
            // x  <= n - num3 - 2
            r += max(n - num3 - num2 + 1, 0);
        }
        return r % 1000000007;
