                    }

                    int left = -1;
                    int right = -1;

                    while (r - l > 1)
                    {
                        int mid = l + (r - l) / 2;
                        if (sums[mid] - sums[i] < sums[i])
                        {
                            l = mid;
                        }
                        else
                        {
                            r = mid;
                        }
                    }


