class Solution {
public:
    int waysToSplit(const vector<int>& nums) {
        static const int mod = 1e9 + 7;
        int n = nums.size(), res = 0;
        vector<int> acc{0};
        partial_sum(nums.begin(), nums.end(), back_inserter(acc));
        for (int x = 1; x <= n - 2; ++x) {
            int sum1 = acc[x] - acc.front();
            int sum2low = acc[x] + sum1;
            int y = lower_bound(acc.begin() + x, acc.end() - 1, sum2low) - acc.begin();
            if (y == x && y < n) ++y;
            int sum2 = acc[y] - acc[x];
            int sum3 = acc.back() - acc[y];
            if (sum3 < sum2) continue;
            int range = (sum3 - sum2) / 2 + acc[y];
            int lasty = upper_bound(acc.begin() + y, acc.end() - 1, range) - acc.begin();
            res += lasty - y;
            if (res >= mod) res -= mod;
        }
        return res;
    }
};
```

