public class Solution {
    public int WaysToSplit(int[] nums) {
        int n = nums.Length;
        int cut = 1;
        int mod = (int)Math.Pow(10, 9) + 7;
        int[] accu = new int[n];
        long tot = 0;

        int tmp = 0;
        for(int i=0; i<n; ++i){
            tmp += nums[i];
            accu[i] = tmp;
