    {
        int lb = n;

        while(l<=h)
        {
            int mid = (l+h)/2;

            if(pre[mid] - x >= x)
            {
                lb = mid;
                h = mid-1;
            }
            else
            {
                l = mid+1;
            }
        }

        return lb;
    }

    int waysToSplit(vector<int>& nums) 
