class Solution {

  int count = 0;
  public int waysToSplit(int[] nums) {
    int[] prefix = new int[nums.length];
    prefix[0] = nums[0];
    for (int i = 1; i < nums.length; i++) {
      prefix[i] += prefix[i - 1] + nums[i];
    }
    // num=.   1,2,2,2,5,0
    // prefix= 1,3,5,7,12,12
    split(nums, prefix, 0, 0, 0);
    return count;
  }

  public void split(int[] nums, int[] prefix, int previousSplitStartIndex, int previousSplitLastIndex, int splitIndex) {

    int previousSplitSum = 0;
    if (splitIndex!=0) {
      previousSplitSum = sum(prefix, previousSplitStartIndex, previousSplitLastIndex-1);
