class Solution {
public:
    int waysToSplit(vector<int>& nums) {
        const int MOD = 1e9+7;
        int n = nums.size();

        vector<int> prefixSum(n, 0);
        for (int i = 0; i < n; i++) {
            prefixSum[i] = nums[i] + (i > 0 ? prefixSum[i - 1] : 0);
        }

        // starting from idx, find an index which makes left <= middle <= right
        // O(lgN)
        auto searchBoundary = [&](int leftSum, int idx, bool searchLeft) {
            int res = -1;

            // at least one element remain for right sum
            int l = idx, r = n - 2;

            while (l <= r) {
                int m = l + (r - l) / 2;

                // [idx...m], [m + 1 ... n - 1]
                int middleSum = prefixSum[m] - prefixSum[idx - 1];
                int rightSum = prefixSum[n - 1] - prefixSum[m];

                if (middleSum >= leftSum && rightSum >= middleSum) {
                    res = m;
                    if (searchLeft) {
                        // try to lower m
                        r = m - 1;
                    } else {
                        l = m + 1;
                    }
                } else if (middleSum < leftSum) {
                    // middlesum is too small, try to expand middle part.
                    l = m + 1;
                } else {
                    r = m - 1;
                }
            }

            return res;
        };


        // O(N)
        int cnt = 0;
        for (int i = 1; i < n; i++) {

            // sum[0...i-1]
            int leftSum = prefixSum[i - 1];

            // sum[i...n-1]
            int remainSum = prefixSum[n - 1] - prefixSum[i - 1];

            // remain sum is not enough for leftsum + middlesum
            if (remainSum < leftSum * 2) {
                break;
            }

            // search left and right boundary whose value is greater than or equal to left sum
            int left = searchBoundary(leftSum, i, true);
            int right = searchBoundary(leftSum, i, false);

            if (left == -1 || right == -1) {
                continue;
            }

            cnt = (cnt + (right - left + 1)) % MOD;
        }
        return cnt;

        // overall time complexity: O(NlgN)
        // overall space complexity: O(N)
    }
};
```

