public class Solution {
    public int WaysToSplit(int[] nums) {
        var res = 0;
        var mod = 1000000007;
        var n = nums.Length;

        for (var i = 1; i < n; i++) {
            nums[i] += nums[i - 1];
        }

        for (var i = 0; i < n - 2; i++) {
            var start = -1;
            var end = -1;

            var lo = i + 1;
            var hi = n - 2;

            while (lo <= hi) {
                var mid = lo + (hi - lo) / 2;

                if (nums[mid] - nums[i] >= nums[i]) {
                    start = mid;
                    hi = mid - 1;
                } else {
                    lo = mid + 1;
                }
            }

            if (start == -1) continue;
            hi = n - 2;

            while (lo <= hi) {
                var mid = lo + (hi - lo) / 2;

                if (nums[mid] - nums[i] > nums[n - 1] - nums[mid]) {
                    hi = mid - 1;
                } else {
                    end = mid;
                    lo = mid + 1;
                }
            }

            if (end == -1) continue;
            res = (res + (end - start + 1)) % mod;
        }        

        return res;
    }
}
