        let max_rv = memo[n] - memo[min_ci];
        if max_rv < min_cv { continue }

        let mut left = min_ci;
        let mut right = n;
        while left+1 < right {
          let mid = (left+right)/2;
          let rv = memo[n] - memo[mid];
          let cv = memo[mid] - lv;
          if cv <= rv {
            left = mid;
          } else {
            right = mid;
          }
        }
        let max_ci = left;
        result += max_ci - min_ci + 1;
        result %= MOD;
      }

      result as i32
  }
}
