class Solution:
    def waysToSplit(self, nums: List[int]) -> int:
        ps=[nums[0]]
        for i in range(1,len(nums)):ps.append(ps[-1]+nums[i])
        ans=0
        for i in range(len(nums)-1):
            if ps[i]*3>ps[-1]:break
            l,r,x=i,len(nums),float('inf')
            while l<=r:
                m=(l+r)//2
                if m>=len(ps):break
                ms=ps[m]-ps[i]
                rs=ps[-1]-(ms+ps[i])
                if ms<=rs and ms>=ps[i]:
                    x=min(x,m)
                    r=m-1
                elif ms<ps[i]:
                    l=m+1
                else:
                    r=m-1

            if x==i:x+=1
            l,r,y=i,len(nums),-float('inf')
            while l<=r:
                m=(l+r)//2
                if m>=len(ps):break
                ms=ps[m]-ps[i]
                rs=ps[-1]-(ms+ps[i])
                if ms<=rs and ms>=ps[i]:
                    y=max(y,m)
                    l=m+1
                elif ms<ps[i]:
                    l=m+1
                else:
                    r=m-1
            if y==len(nums)-1:y-=1
            if x==float('inf') or y==-float('inf'):continue
            ans+=((y-x)+1)
        return ans%((10**9)+7)
