func WaysToSplit(nums []int) int {

	size := len(nums)
	result := 0

	if size < 3 {
		return result
	}

	modValue := 1_000_000_007

	for i := 1; i < size; i++ {
		nums[i] += nums[i-1]
	}

	i := 0
	j := 1
	k := 1
	for ; i < size-2; i++ {
		if j < i+1 {
			j = i + 1
		}

		for j < size-1 && nums[i]*2 > nums[j] {
			j++
		}

		if k < j {
			k = j
		}

		for k < size-1 && nums[size-1]-nums[k] >= nums[k]-nums[i] {
			k++
		}

		result = (result + k - j) % modValue
	}

	return result
}
