class Solution {
public:
    int waysToSplit(vector<int>& nums) {
        vector<int> pf;
        int res=0;
        pf.resize(nums.size(),0);
        pf[0] = nums[0];

        for(int i=1;i<pf.size();i++)
            pf[i]= pf[i-1]+nums[i];

        int S,L;
        S=pf[pf.size()-1];

        for(int i=0;i<(nums.size()-2);i++)
        {
            L=pf[i];

            vector<int>::iterator a,b;

            a=upper_bound(pf.begin()+i+1,pf.end(),(S+L)/2);
            if(pf[pf.size()-1]==0)
