class Solution {
    public int waysToSplit(int[] nums) {
        // find the prefix sum of the input array
        for (int i = 1; i < nums.length; i++) nums[i] += nums[i-1];
        // store the total sum of the array
        int totalSum = nums[nums.length - 1];
        long count = 0l;
        // for every possible [left sub-array], find the range possible for [mid sub-array]
        for (int i = 0; i <= nums.length - 3; i++) {
            // the [mid sub-array] sum cannot be lesser than the current [left sub-array] sum
            int midRangeLeft = nums[i] * 2;
            // the [mid sub-array] sum cannot be greater than the [right sub-array] sum
            int midRangeRight = nums[i] + ((totalSum - nums[i]) / 2);
            if (midRangeLeft <= midRangeRight) {
                // perform a binary search to find the left and right extremes of the [mid sub-array]
                long leftIndex = getCeilingIndex(nums, midRangeLeft, i + 1);
                long rightIndex = getFloorIndex(nums, midRangeRight,  i + 1);
                // if the extremes are valid, compute the count of possible triplets and add to answer
                if (leftIndex != -1 && rightIndex != -1) {
                    count = (count + (rightIndex - leftIndex + 1)) % 1_000_000_007l;
                }
            }
        }
        return (int)count;
    }

    // find the left-most index arr[i] which is >= target element after arr[left]
    int getCeilingIndex(int[] arr, int target, int left) {
        int ans = -1, right = arr.length - 2;
        while (left <= right) {
            int mid = left + (right - left) / 2;
            if (arr[mid] >= target) {
                ans = mid;
                right = mid - 1;
            } else {
                left = mid + 1;
            }
        }
        return ans;
    }

    // find the right-most index arr[i] which is <= target element after arr[left]
    int getFloorIndex(int[] arr, int target, int left) {
        int ans = -1, right = arr.length - 2;
        while (left <= right) {
            int mid = left + (right - left) / 2;
            if (arr[mid] <= target) {
                ans = mid;
                left = mid + 1;
            } else {
                right = mid - 1;
            }
        }
        return ans;
    }
}
