constexpr int mod = (int)1e9 + 7;

class Solution {
public:
    int waysToSplit(vector<int>& nums) {
        int n = nums.size();
        vector<int> pre(n);
        for (int i = 0; i < n; ++i) {
            pre[i] += i == 0 ? 0 : pre[i-1];
            pre[i] += nums[i];
        }
        int total = pre[n-1];

        int ret = 0;
        // i is the inclusive last element of the 1st block
        for (int i = 0; i < n-2; ++i) {
			// the 1st block can only be a third of the total
            if (pre[i]*3 > total) break;
			// for the 2nd block, the lowest end value is two times the 1st block's value
            int low = 2 * pre[i];
			// the remaining total not used in the first block gets split up between the 2nd and 3rd block
			// the first bad value is where the 2nd block has more value than the 3rd block
            int high_bad = pre[i] + (total - pre[i]) / 2 + 1;

			// search for the first good element that comes after 1st block's end
            auto left = lower_bound(pre.begin()+i+1, pre.end(), low);
			// 2nd block lower end was too big!
            if (*left >= high_bad) continue;

			// we know left is a legal end for 2nd block
			// now search for the first too-big end (that can't be the last element)
            auto right = lower_bound(left+1, pre.end()-1, high_bad);

            ret = (ret + distance(left, right)) % mod;
        }
        return ret;
    }
};
