            int ans = -1;

            while(l <= r){
                m = l + (r - l )/2;
                if(nums[m] >= 2L* nums[i] ){
                    ans = m;
                    r = m -1;
                }else{
                    l = m +1;
                }
            }

            if(ans == -1)continue;

            l = ans; r = n - 2;
            int ans2 = -1;

            while(l <= r){
                m = l + (r - l )/2;

                if(nums[n-1] + nums[i] >= 2*nums[m]  ){
                    ans2 = m;
                    l  = m + 1;
                }else{
                    r = m - 1; 
                }
            }
            if(ans2 == -1)continue;

            res = (res + ans2 - ans + 1)%1000000007;
        }

        return res;
    }
}
