class Solution {
public:
    int mod=1e9+7;

    int waysToSplit(vector<int>& v) {
        int i;
        int n=v.size();
        vector<int> pre(n,0);


        bool flag=false;
        pre[0]=v[0];
        int full=v[0];

        if (v[0]!=0){
            flag=true;
        }

        for (i=1; i<n; i++){
            pre[i]=pre[i-1]+v[i];
            full+=v[i];
            if (v[i]!=0){
                flag=true;
            }
        }


        if (!flag){
            int ans=0;

            for (i=1; i<n-1; i++){
                ans=(ans%mod + i%mod)%mod;
            }
         return ans;
        }
        int ans=0,sum=0;

        for (i=0; i<n; i++){
           sum+=v[i];
           int rem=full-sum;
           int partition=rem/2;



           auto it=upper_bound(pre.begin(),pre.end(),sum+partition);
           auto it1=lower_bound(pre.begin(),pre.end(),2*sum);

           if (it!=pre.end()){
               if (it1-pre.begin()>it-pre.begin()){
                   continue;
               }

               int curr1=it-pre.begin();
               int curr2=it1-pre.begin();

               int idx=(curr1-max(curr2,i+1));
               ans=(ans%mod + idx%mod)%mod;
           }

        }

     return ans;
    }
};
