class Solution {
    var isSorted = { A: IntArray -> A.withIndex().all{ (i, _) -> (0 < i && A[i - 1] <= A[i]) || (i == 0) } }
    var isMonotonic = { A: IntArray -> isSorted(A) || isSorted(A.reversed().toIntArray()) }
}
```

