class Solution {
public:

    bool isMonotonic(vector<int>& v) {
        int flag;
        //if flag=1 then increaseing, if flag==-1 the decreasing

        int n=v.size();

		//if number of element is 1 or 2 the array is always monotonic
        if(n<=2)
            return true;

		//steps to remove duplicate elements
        vector<int>::iterator ip;
        ip = std::unique(v.begin(), v.begin() + n);
        v.resize(std::distance(v.begin(), ip));

		//setting the flag value wrt the first 2 elements
        if(v[0]<v[1])
            flag=1;
        else
            flag=-1;
        int p;
        for(int i=1;i<v.size()-1;i++){
            if(v[i]<v[i+1])
                p=1;
            else
                p=-1;
            if(p!=flag)
                return false;       
        }
        return true;
    }
};

