class Solution {
public:
    bool isMonotonic(vector<int>& nums) {
        int res = 0;
        int n = nums.size();
        for(int i =0;i<n-1; i++){
            if(nums[i] > nums[i+1]){
                if(res == 0) res = -1;
                else if(res == 1) return false;
            }
            else if(nums[i] < nums[i+1]){
                if(res == 0) res = 1;
                else if(res == -1) return false;
            }
        }
        return true;
    }
};
