class Solution {
public:
    bool isMonotonic(vector<int>& nums) {
        if( nums.size() <= 2 ) return 1;
        int n = nums.size(), d = 0;

        // set value of d ( 1 or -1 ) when change occur first time 
        for(int i=1; i<n; i++){
            int dt = nums[i] - nums[i-1];
            if( d == 0 && dt != 0 ) d = dt > 0 ? 1 : -1;
            else if( dt*d < 0 ) return 0;
        }
        return 1;
    }
};
```

