class Solution:
    def pathsWithMaxScore(self, board: List[str]) -> List[int]:
        m = len(board)
        n = len(board[0])

        from functools import lru_cache
        @lru_cache(maxsize = None)
        def dfs(i,j,count):
            if i == 0 and j == 0:
                return 0, 1
            if i < 0 or j < 0 or board[i][j] == "X":
                return -float("inf"), -float("inf")

            count = 0

            temp = int(board[i][j]) if board[i][j] != "S" else 0

            c1, r1 = dfs(i-1,j,count+1)
            c2, r2 = dfs(i,j-1,count+1)
            c3, r3 = dfs(i-1,j-1,count+1)

            max_path = max(c1,c2,c3)

            for x,y in ((c1,r1),(c2,r2),(c3,r3)):
                if x == max_path:
                    count += y

            return temp+max_path, count


        a, b = dfs(m-1,n-1,0)

        return [a%(10**9+7), b%(10**9+7)] if a!= -float("inf") or b != -float("inf") else [0, 0]
