      mat = new char[m][n];
      for (int i = 0; i < m; i++) {
        String s = board.get(i);
        for (int j = 0; j < n; j++) {
          mat[i][j] = s.charAt(j);
        }
      }

      Data data = f(m - 1, n - 1);
      return data.sum < 0 ? new int[]{0, 0}
          : new int[]{data.sum, data.count};
    }

    private Data f(int i, int j) {

      if (mat[i][j] == 'X') {
        return new Data(Integer.MIN_VALUE, 0);
      }
      if (i == 0 && j == 0) {
        return new Data(0, 1);
      }
      String key = i + "~" + j;
      if (cache.containsKey(key)) {
        return cache.get(key);
