fun pathsWithMaxScore(board: List<String>): IntArray {
    val n = board.size
    val score = Array(n, { i -> Array(n, { j -> intArrayOf(0, 0) }) }) 
    for (i in n - 1 downTo 0) {
        columeLoop@ for (j in n - 1 downTo 0) {
            when (board[i][j]) {
                'X' -> continue@columeLoop
                'S' -> score[i][j][1] = 1
                else -> {
                    val lower = score.elementAtOrNull(i + 1)?.elementAtOrNull(j) ?: intArrayOf(0, 0)
                    val right = score.elementAtOrNull(i)?.elementAtOrNull(j + 1) ?: intArrayOf(0, 0)
                    val lowerRight = score.elementAtOrNull(i + 1)?.elementAtOrNull(j + 1) ?: intArrayOf(0, 0)
                    val sum = calculateScore(arrayOf(lower, right, lowerRight))
                    if (sum[1] != 0) {
                        val self = if (board[i][j] == 'E') 0 else board[i][j] - '0'
                        score[i][j] = intArrayOf(sum[0] + self, sum[1])
                    }
                }
            }
        }
    }
    return score[0][0]
}

private fun calculateScore(arrays: Array<IntArray>): IntArray {
    val mod = (Math.pow(10.0, 9.0) + 7).toInt()
    return arrays.reduce { acc, score -> if (score[0] > acc[0]) score else if (score[0] == acc[0]) intArrayOf(acc[0], (acc[1] + score[1])%mod) else acc }
}

