class Solution { 
    public int[] pathsWithMaxScore(List<String> board) {
        int traverse[][]=new int[][]{{-1,0},{0,-1},{-1,-1}};
        int n=board.size(),m=board.get(0).length(),mod=(int)1e9+7;
         board.set(n-1,board.get(n-1).substring(0,m-1)+"0");
        int dp[][][]=new int[n][m][2];
        dp[0][0][1]=1;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(board.get(i).charAt(j)=='X')continue;
                for(int t[] : traverse){
                    if(i+t[0]<0||i+t[0]>=n||j+t[1]<0||j+t[1]>=m)continue;
                    if(dp[i+t[0]][j+t[1]][0]+(board.get(i).charAt(j)-'0')==dp[i][j][0])
                        dp[i][j][1]=(dp[i][j][1]+dp[i+t[0]][j+t[1]][1])%mod;
