                    dp[i][j][1]=dp[i+t[0]][j+t[1]][1];
                }
                }
            }
        }
        return new int[]{dp[n-1][m-1][1]==0 ? 0 : dp[n-1][m-1][0],dp[n-1][m-1][1]};
    }
}
