    pair<ll int,ll int> fun(int i,int j,vector<string>&mat,int n)
    {
        if(i==n-1 && j==n-1)
            return {0,1};
        if(i>=n || j>=n)
            return {INT_MIN,0};
        if(mat[i][j]=='X')
            return {INT_MIN,0};
        if(dp[i][j][0]!=-1)
            {          
            pair<ll int,ll int>p={dp[i][j][0],dp[i][j][1]};
            return p;
            }
        pair<ll int,ll int>p={INT_MIN,0};
        auto p1=fun(i+1,j+1,mat,n);
        p=p1;
        auto p2=fun(i,j+1,mat,n);
        if(p2.first>p.first)
        {
            p.first=p2.first;
            p.second=p2.second;
        }
        else if(p2.first==p.first)
            p.second+=p2.second;
        auto p3=fun(i+1,j,mat,n);
        if(p3.first>p.first)
        {
            p.first=p3.first;
            p.second=p3.second;
        }
        else if(p3.first==p.first)
            p.second+=p3.second;
        if(p.first!=INT_MIN && mat[i][j]>='1' && mat[i][j]<='9')
            p.first+=mat[i][j]-'0';
        dp[i][j][0]=p.first;
        dp[i][j][1]=p.second;
        p.second%=M;
        return p;


