        result[1] = pathsWithMaxScore(board, rows - 1, cols - 1, m1, m2) + 1;
        return result;
    }

    public int maxScore(List<String> board, int i, int j, Integer[][] m) {
        if(i == 0 && j == 0) {
            return 0;
        }
        if(m[i][j] != null) {
            return m[i][j];
        }
        int val = 0;
        if(board.get(i).charAt(j) != 'S') {
            val = Character.getNumericValue(board.get(i).charAt(j));
        }
        int up = Integer.MIN_VALUE;
        if(i > 0 && board.get(i - 1).charAt(j) != 'X') {
            up = maxScore(board, i - 1, j, m) + val;
        }
        int left = Integer.MIN_VALUE;
        if(j > 0 && board.get(i).charAt(j - 1) != 'X') {
            left = maxScore(board, i, j - 1, m) + val;
        }
        int leftUp = Integer.MIN_VALUE;
        if(i > 0 && j > 0 && board.get(i - 1).charAt(j - 1) != 'X') {
            leftUp = maxScore(board, i - 1, j - 1, m) + val;
        }
        int result = Math.max(up, Math.max(left, leftUp));
        return m[i][j] = result;
    }

    public int pathsWithMaxScore(List<String> board, int i, int j, Integer[][] m1, Integer[][] m2) {
        if(i == 0 && j == 0) {
            return 0;
        }

        if(m2[i][j] != null)
            return m2[i][j];

        int up = Integer.MIN_VALUE;
        if(i > 0 && board.get(i - 1).charAt(j) != 'X') {
            if(m1[i - 1][j] != null)
                up = m1[i - 1][j];
        }
        int left = Integer.MIN_VALUE;
        if(j > 0 && board.get(i).charAt(j - 1) != 'X') {
            if(m1[i][j - 1] != null)
                left = m1[i][j - 1];
        }
        int leftUp = Integer.MIN_VALUE;
        if(i > 0 && j > 0 && board.get(i - 1).charAt(j - 1) != 'X') {
            if(m1[i - 1][j - 1] != null)
                leftUp = m1[i - 1][j - 1];
        }
        int result = Math.max(up, Math.max(left, leftUp));
        int count = -1;
        if(up == result && i > 0 && board.get(i - 1).charAt(j) != 'X') {
            count += pathsWithMaxScore(board, i - 1, j, m1, m2) + 1;
            count %= MOD;
        }
        if(left == result && j > 0 && board.get(i).charAt(j - 1) != 'X') {
            count += pathsWithMaxScore(board, i, j - 1, m1, m2) + 1;
            count %= MOD;
        }
        if(leftUp == result && i > 0 && j > 0 && board.get(i - 1).charAt(j - 1) != 'X') {
            count += pathsWithMaxScore(board, i - 1, j - 1, m1, m2) + 1;
            count %= MOD;
        }
        m2[i][j] = count;
        return count;
    }

