/// Do DFS keeping in mind the no of max paths
int **dp_score;
int **dp_path_cnt;

#define MOD (1000000007ULL)

int GetMaxScore(char ** board, int row, int col, int *max_path_cnt, int brd_sz)
{
    if((row >= brd_sz) || (col >= brd_sz))
    {
        *max_path_cnt = 0;
        return 0;   
    }
    if(board[row][col] == 'X')
    {        
        *max_path_cnt = 0;
        return 0;  
    }
    if(board[row][col] == 'S')
    {
        *max_path_cnt = 1;
        return 0;   
    }
    if(dp_path_cnt[row][col] != INT_MAX)
    {
        *max_path_cnt = dp_path_cnt[row][col];
        return dp_score[row][col];
    }
    int max_score;
    int max_cnt = 0;
    int bottom_score;
    int bot_right_score;
    int right_score;
    int bottom_path;
    int bot_right_path;
    int right_path;

    bottom_score = GetMaxScore(board, row+1, col, &bottom_path, brd_sz);
    bot_right_score = GetMaxScore(board, row+1, col+1, &bot_right_path, brd_sz);
    right_score = GetMaxScore(board, row, col+1, &right_path, brd_sz);

    max_score = bottom_score;
    if(max_score < bot_right_score)
        max_score = bot_right_score;

    if(max_score < right_score)
        max_score = right_score;

    if(max_score == bottom_score)
        max_cnt+=bottom_path;
    if(max_score == bot_right_score)
        max_cnt+=bot_right_path;
    if(max_score == right_score)
        max_cnt+=right_path;

    *max_path_cnt = max_cnt%MOD;
    if(board[row][col] != 'E')
        max_score = max_score + board[row][col] - '0';
    if(0 == max_cnt)
        max_score = 0;
    dp_path_cnt[row][col] = max_cnt%MOD;
    dp_score[row][col]    = max_score%MOD;
    return max_score%MOD;
}

/**
 * Note: The returned array must be malloced, assume caller calls free().
 */
int* pathsWithMaxScore(char ** board, int boardSize, int* returnSize){


    /// Create DP storage and initialize
    dp_score = malloc(boardSize*sizeof(int*));
    dp_path_cnt = malloc(boardSize*sizeof(int*));
    for(int k=0; k<boardSize;k++)
    {
        dp_score[k]    = malloc(boardSize*sizeof(int));
        dp_path_cnt[k] = malloc(boardSize*sizeof(int));
        for(int j=0;j<boardSize;j++)
        {   
            dp_score[k][j] = INT_MAX;
            dp_path_cnt[k][j] = INT_MAX;
        }
    }

    int *res = malloc(10*sizeof(int));
    res[0] = GetMaxScore(board, 0, 0, &res[1], boardSize);
    *returnSize = 2;
    return res;
}

