public int[] pathsWithMaxScore(List<String> board) {
	int mod = (int)1e9 + 7;
	int[][] dirs = { { -1, -1 }, { -1, 0 }, { 0, -1 } };
	int n = board.size();
	int[][][] dp = new int[n][n][2];
	dp[0][0] = new int[] { 0, 1 };
	for (int i = 0; i < n; i++) {
		for (int j = 0; j < n; j++) {
			char c = board.get(i).charAt(j);
			if (c == 'X') continue;
			int num = c == 'S' ? 0 : c - '0';
			for (int[] dir : dirs) {
				int ii = i + dir[0], jj = j + dir[1];
				if (ii < 0 || jj < 0) continue;
				if ((ii == 0 && jj == 0) || dp[ii][jj][0] > 0) {
					if (dp[ii][jj][0] + num > dp[i][j][0]) {
						dp[i][j][0] = dp[ii][jj][0] + num;
						dp[i][j][1] = dp[ii][jj][1];
					} else if (dp[ii][jj][0] + num == dp[i][j][0]) {
						dp[i][j][1] = (dp[i][j][1] + dp[ii][jj][1]) % mod;
					}
				}
			}
		}
	}
	return dp[n - 1][n - 1];
}

