class Solution {
    int mod = 1_000_000_007;
    public int[] pathsWithMaxScore(List<String> board) {
        int n = board.size();
        int m = board.get(0).length();
        int[][] max = new int[n][m];
        long[][] nums = new long[n][m];
        for (int[] x : max) {
            Arrays.fill(x, -1);
        }
        max[n - 1][m - 1] = 0;
        nums[n - 1][m - 1] = 1;
        for (int i = n - 1; i >= 0; i--) {
            for (int j = m - 1; j >= 0; j--) {
                if (i == n - 1 && j == m - 1) {
                    continue;
                }
                char c = board.get(i).charAt(j);
                if (c != 'X') {
                    int tmp = i == 0 && j == 0 ? 0 : c - '0';
                    if (i == n - 1) {
                        if (max[i][j + 1] != -1) {
                            max[i][j] = Math.max(max[i][j], max[i][j + 1] + tmp);
                            nums[i][j] = (nums[i][j] + nums[i][j + 1]) % mod;
                        }
                        continue;
                    }
                    if (j == m - 1) {
                        if (max[i + 1][j] != -1) {
                            max[i][j] = Math.max(max[i][j], max[i + 1][j] + tmp);
                            nums[i][j] = (nums[i][j] + nums[i + 1][j]) % mod;
                        }
                        continue;
                    }
                    if (max[i][j + 1] != -1) {
                        max[i][j] = Math.max(max[i][j], max[i][j + 1] + tmp);
