class Solution {
    public int[] pathsWithMaxScore(List<String> board) {
        int m = board.size();
        int n = board.get(0).length();
        long[][] countDP = new long[m+1][n+1];
        long[][] sumDP = new long[m+1][n+1];
        int mod = 1000000007;
        countDP[m][n] = 1;
        for(int i = m-1; i >= 0; i--) {
            for(int j = n-1; j>=0; j--) {
                if (board.get(i).charAt(j) != 'X') {
                    long max = Math.max(sumDP[i][j+1],Math.max(sumDP[i+1][j],sumDP[i+1][j+1]));
                    sumDP[i][j] = getValue(i,j,board) + max;
                    countDP[i][j]  = sumDP[i][j+1]   == max ? countDP[i][j+1]   : 0;
                    countDP[i][j]  = (countDP[i][j] + (sumDP[i+1][j]   == max ? countDP[i+1][j]   : 0))%mod;
                    countDP[i][j] =  (countDP[i][j] + (sumDP[i+1][j+1] == max ? countDP[i+1][j+1] : 0))%mod;
                }
            }
        }

        int maxSum = (int)sumDP[0][0];
        int maxPathCount = (int)countDP[0][0];
        return new int[]{maxPathCount!=0?maxSum:0,maxPathCount};
    }
    private long getValue(int x, int y, List<String> board) {
        char c = board.get(x).charAt(y);
        if (c >= '1' && c <= '9') return c-'1'+1L;
        else return 0L;
    }
}
