                chess[i][j] = board.get(i).charAt(j);
                if(i == m - 1 && j == m - 1){
                  chess[i][j] = '0';
                }
            }
        }
        dfs(chess, sx, sy, ex, ey, map, 0);
        return new int[]{maxSum, map.getOrDefault(maxSum, 0)};
    }
    public void dfs(char [][]chess, int sx, int sy, int ex, int ey, Map<Integer, Integer> map, int sum){
        if(sx < 0 || sx >= chess.length || sy < 0 || sy >= chess.length || chess[sy][sx] == 'X'){
            return;
        }

        if(sx == ex && sy == ey){
            map.put(sum, map.getOrDefault(sum, 0) + 1);
            maxSum = Math.max(maxSum, sum);
            return;
        }
        sum += Integer.valueOf(chess[sy][sx] - '0');
        dfs(chess, sx - 1, sy    , ex, ey, map, sum);
        dfs(chess, sx    , sy - 1, ex, ey, map, sum);
        dfs(chess, sx - 1, sy - 1, ex, ey, map, sum);


    }
}
