class Solution {
    int MOD = 1000000007;
    public int[] pathsWithMaxScore(List<String> board) {
        //dp[i][j][0] means score = Max among dp[i+1][j][0], dp[i+1][j+1][0], dp[i][j+1][0] + board[i][j]
        //dp[i][j][1] means paths = sum among dp[i+1][j][1], dp[i+1][j+1][1], dp[i][j+1][1] if value of each equals to max
        int m = board.size();
        int n = board.get(0).length();
        int[][][] dp = new int[m][n][2];
        dp[m-1][n-1][1] = 1;

        for(int j = n - 2, i = m - 1; j >= 0; j--){
            char c = getBoard(board, i,  j);
            if(c == 'X'){
                break;
            }
            else{
                int v = Character.isDigit(c) ? Integer.parseInt(String.valueOf(c)) : 0;
                dp[i][j][0] = (dp[i][j+1][0] + v) % MOD;
                dp[i][j][1] = dp[i][j+1][1] % MOD;
            }
        }
        for(int j = n - 1, i = m - 2; i >= 0; i--){
            char c = getBoard(board, i,  j);
            if(c == 'X'){
                break;
            }
            else{
                int v = Character.isDigit(c) ? Integer.parseInt(String.valueOf(c)) : 0;
                dp[i][j][0] = (dp[i + 1][j][0] + v) % MOD;
                dp[i][j][1] = dp[i + 1][j][1] % MOD;
            }
        }

        for(int i = m - 2; i >= 0; i--){
            for(int j = n - 2; j >= 0; j--){
                char c = getBoard(board, i, j);
                if(c != 'X'){
                    int v = Character.isDigit(c) ? Integer.parseInt(String.valueOf(c)) : 0;
                    int max = Math.max(dp[i+1][j][0], Math.max(dp[i+1][j+1][0], dp[i][j+1][0]));
                    int way = 0;
                    if(max == dp[i+1][j][0] && dp[i+1][j][1] != 0){
                        way += dp[i+1][j][1] % MOD;
                    }
                    if(max == dp[i+1][j+1][0] && dp[i+1][j+1][1] != 0){
                        way += dp[i+1][j+1][1] % MOD;
                    }
                    if(max == dp[i][j+1][0] && dp[i][j+1][1] != 0){
                        way += dp[i][j+1][1] % MOD;
                    }
					//way == 0 means all block
                    dp[i][j][0] = way == 0 ? 0 : (max + v) % MOD;
                    dp[i][j][1] = way % MOD;
                }
            }
        }

        return dp[0][0];
    }

    private char getBoard(List<String> board, int i,  int j){
        return board.get(i).charAt(j);
    }
}
