    pair<long long,long long> helper(int i,int j,int n,int m,vector<string>&arr,vector<vector<pair<long long,long long>>>&dp){

        if(i<0 || j<0 || i>=n || j>=m || arr[i][j]=='X'){
           return {-1e9,-1e9}; 
        }
        if(i==0 && j==0){
            return {0,1};
        }
        if(dp[i][j].first!=-1 && dp[i][j].second!=-1){
            return dp[i][j];
        }
        pair<long long,long long> ans1=helper(i-1,j,n,m,arr,dp);
        pair<long long,long long> ans2=helper(i,j-1,n,m,arr,dp);
        pair<long long,long long> ans3=helper(i-1,j-1,n,m,arr,dp);
        long long score;
        score=max(ans1.first%mod,max(ans2.first%mod,ans3.first%mod));
        pair<long long,long long> ans={0,0};
        ans.first=score%mod;
        long long paths=0;
        if(ans1.first==score){
            paths=paths%mod+ans1.second%mod;
        }
        if(ans2.first==score){
            paths=paths%mod+ans2.second%mod;
        }
        if(ans3.first==score){
            paths=paths%mod+ans3.second%mod;
        }
        char ch=arr[i][j];
        if(ch!='E' && ch!='S'){
            ans.first=ans.first+(ch-'0');
        }
        ans.second=paths%mod;
        return dp[i][j]=ans;
    }
    vector<int> pathsWithMaxScore(vector<string>& board) {

        int n=board.size();
        int m=board[0].size();
        vector<vector<pair<long long,long long>>> dp(n,vector<pair<long long,long long>>(m,{-1,-1}));
        pair<long long,long long> p=helper(n-1,m-1,n,m,board,dp);
        vector<int> ans;
        if(p.first<0 || p.second<0){
            return {0,0};
        }
        ans.push_back(p.first);
        ans.push_back(p.second);
        return ans;
    }
};

