class Solution {
public:
    vector<int> pathsWithMaxScore(vector<string>& board) {
        int mod = 1000000007;
        int n = board.size();

        // Initialize dp array and cnt array
        vector<vector<int>> dp(n + 1, vector<int>(n + 1));
        vector<vector<int>> cnt(n + 1, vector<int>(n + 1));

        // Convert the start and end cells to '0'
        board[n - 1][n - 1] = board[0][0] = '0';

        // Set the count of paths for the destination cell to 1
        cnt[n - 1][n - 1] = 1;

        // Iterate through the board in reverse order
        for (int i = n - 1; i >= 0; --i) {
            for (int j = n - 1; j >= 0; --j) {
                if (board[i][j] != 'X') {
                    // Find the maximum value among the three possible moves
                    int m = max({dp[i + 1][j], dp[i][j + 1], dp[i + 1][j + 1]});

                    // Calculate the new dp value for the current cell
                    dp[i][j] = (board[i][j] - '0') + m;

                    // Update the count of paths for the current cell based on the moves that contribute to the maximum value
                     // If the cell below has the same maximum value
            if (dp[i + 1][j] == m)
                cnt[i][j] = (cnt[i][j] + cnt[i + 1][j]) % mod;

            // If the cell to the right has the same maximum value
            if (dp[i][j + 1] == m)
                cnt[i][j] = (cnt[i][j] + cnt[i][j + 1]) % mod;

            // If the diagonal cell has the same maximum value
            if (dp[i + 1][j + 1] == m)
                cnt[i][j] = (cnt[i][j] + cnt[i + 1][j + 1]) % mod;
                }
            }
        }

        // Return the result as a vector
       // Return a pair containing two values:
// 1. If cnt[0][0] is non-zero (paths are possible), return dp[0][0], otherwise return 0
// 2. Return the value of cnt[0][0] as the second value of the pair
return {cnt[0][0] ? dp[0][0] : 0, cnt[0][0]};

    }
};

