var pathsWithMaxScore = function(board) {
    const n = board.length, m = board[0].length, mod = 1_000_000_007;
    let prev = Array.from({ length: m + 1 }, () => [0, 0]);

    prev[0][1] = 1;

    for (let i = 0; i < n; i < i++) {
        const cur = Array.from({ length: m + 1 }, () => [0, 0]);

        for (let j = 0; j < m; j++) {
            if (board[i][j] == "X") continue;

            const prevPositions = [prev[j], prev[j + 1], cur[j]];
            const maxPrevSum = Math.max(...prevPositions.map(([sum]) => sum));            
            cur[j + 1] = prevPositions.reduce((acc, [sum, ways]) => 
                    (sum === maxPrevSum ? [sum, (acc[1] + ways) % mod] : acc), [0, 0]);
            if (cur[j + 1][1] > 0)
                cur[j + 1][0] += isNaN(board[i][j]) ? 0 : +board[i][j];
        }

        prev = cur;
    }

    return prev.at(-1);
};
