// The code below wasn't writen to be readable, it is for runtime optimization.

/**
 * @param {string[]} board
 * @return {number[]}
 */
var pathsWithMaxScore = function (board) {
  const MOD = 1000000007;
  const n = board.length;

  const maxRow = Array(n).fill(0);
  maxRow[-1] = 0;
  const cntRow = Array(n).fill(0);
  cntRow[-1] = 0;

  function getVal(r, c) {
    const char = board[r][c];
    if (char >= "0" && char <= "9") return char.charCodeAt() - 48;
    return 0;
  }

  let maxBak, cntBak, maxCur, cntCur;
  for (let r = 0; r < n; r++) {
    maxCur = 0;
    cntCur = 0;

    for (let c = 0; c < n; c++) {
      maxBak = maxCur;
      cntBak = cntCur;
      maxCur = maxRow[c];
      cntCur = cntRow[c];
      const v = getVal(r, c);

      if (board[r][c] === "E") {
        cntRow[c] = 1;
        maxRow[c] = v;
        continue;
      }
      if (board[r][c] === "X" || !(cntRow[c] || cntRow[c-1] || cntBak)) {
        cntRow[c] = 0;
        maxRow[c] = 0;
        continue;
      }

      if (maxRow[c] < maxRow[c-1]) {
        cntRow[c] = cntRow[c-1];
        maxRow[c] = maxRow[c-1];
      } else if (maxRow[c] === maxRow[c-1]) {
        cntRow[c] = (cntRow[c] + cntRow[c-1]) % MOD;
      }

      if (maxRow[c] < maxBak) {
        cntRow[c] = cntBak;
        maxRow[c] = maxBak;
      } else if (maxRow[c] === maxBak) {
        cntRow[c] = (cntRow[c] + cntBak) % MOD;
      }

      maxRow[c] += v;
    }
  }

  return [maxRow[n - 1], cntRow[n - 1]];
};
```

